#import "ZXCode39ExtendedModeTestCase.h"
#import "ZXCode39Reader.h"

@implementation ZXCode39ExtendedModeTestCase

- (void)testDecodeExtendedMode {
  [self doTestExpectedResult:[NSString stringWithFormat:@"%C%C%C%C%C%C%C%C\b\t\n%C\f\r%C%C%C%C%C%C%C%C%C%C%C%C%C%C%C%C%C%C", 0x0000, 0x0001, 0x0002, 0x0003, 0x0004, 0x0005, 0x0006, 0x0007, 0x000b, 0x000e, 0x000f, 0x0010, 0x0011, 0x0012, 0x0013, 0x0014, 0x0015, 0x0016, 0x0017, 0x0018, 0x0019, 0x001a, 0x001b, 0x001c, 0x001d, 0x001e, 0x001f] encodedResult:@""];
  [self doTestExpectedResult:@" !\"#$%&'()*+,-./0123456789:;<=>?" encodedResult:@""];
  [self doTestExpectedResult:@"@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\\]^_" encodedResult:@""];
  [self doTestExpectedResult:@"`abcdefghijklmnopqrstuvwxyz{|}~" encodedResult:@""];
}

- (void)doTestExpectedResult:(NSString *)expectedResult encodedResult:(NSString *)encodedResult {
  ZXCode39Reader *reader = [[ZXCode39Reader alloc] initUsingCheckDigit:NO extendedMode:YES];
  ZXBitMatrix *matrix = [ZXBitMatrix parse:encodedResult setString:@"1" unsetString:@"0"];
  ZXBitArray *row = [matrix rowAtY:0 row:nil];
  ZXResult *result = [reader decodeRow:0 row:row hints:nil error:nil];
  XCTAssertEqualObjects(expectedResult, result.text);
}

@end
